// src/App.js

import React from "react";
import Login from "./components/Login";
import { useAuth0 } from "./react-auth0-wrapper";
import Blackout from "./components/Blackout"


function App() {
  const { loading } = useAuth0();

  if (loading) {
    return (
      <div>Loading...</div>
    );
  }

  let timeoutID;

  function setup() {
    window.addEventListener("mousemove", resetTimer);
    // this.Blackout.addEventListener("mousedown", resetTimer, false);
    // this.Blackout.addEventListener("keypress", resetTimer, false);
    // this.Blackout.addEventListener("DOMMouseScroll", resetTimer, false);
    // this.Blackout.addEventListener("mousewheel", resetTimer, false);
    // this.Blackout.addEventListener("touchmove", resetTimer, false);
    // this.Blackout.addEventListener("MSPointerMove", resetTimer, false);

    startTimer();
}
setup();

function startTimer() {
  // wait 2 seconds before calling goInactive
  timeoutID = window.setTimeout(goInactive, 2000);
}

function resetTimer(e) {
  window.clearTimeout(timeoutID);

  goActive();
}

function goInactive() {
  alert("inactive")
  document.getElementById("blackOutScreen").style.height="100%" 
}

function goActive() {
  alert("active")
  document.getElementById("blackOutScreen").style.height="0%" 

  startTimer();
}  

const divStyle = {
  color: 'blue',

};

  return (
    <div style={divStyle} id="blackOutScreen" className="App">
      {/* <Blackout /> */}
      {/* <header>
        <Login />
      </header> */}
    </div>
  );
}

export default App;
