import React from "react"
import { Redirect } from "react-router-dom";
import { AuthConsumer } from "../authContext";

const AdminMenu = () => (
    <AuthConsumer>
    {({ user }) => (

    <Can
        role={user.role}
        perform="dashboard-page:visit"
        yes={() => (
            <button onClick={alert("test")}>test</button>
        )}
        no={() => <Redirect to="/" />}
      />
     )}
  </AuthConsumer>
)

export default AdminMenu
