import React from "react"

function Blackout() {
    
    const onClick = () => {
        let blackOutButton = document.getElementById("blackOutButton")
        blackOutButton.classList.toggle("is-active")
        if (blackOutButton.classList.contains("is-active")){            
            document.getElementById("navBar").style.height="100%"
            document.getElementById("navBar").style.width="100%"                   
        } else {
            document.getElementById("navBar").style.height="0px"            
        }
    }
    return (
        <div className="blackOut" id="blackOutButton" onClick={onClick}>

        </div>
    )
} 

export default Blackout
