import React from "react"

const Blackout = () => {
    
    const onClick = () => {
        let blackOutScreen = document.getElementById("blackOutScreen")
        blackOutScreen.classList.toggle("is-active")
        if (blackOutScreen.classList.contains("is-active")){            
            document.getElementById("blackOutScreen").style.height="100%"                   
        } else {
            document.getElementById("navBar").style.height="0px"            
        }
    }
    return (
        <div 
            style={{width:'100%', backgroundColor:'000000'}}
            className="blackOut" 
            id="blackOutScreen" 
            onClick={onClick}>
        </div>
    )
} 

export default Blackout
