// src/index.js

import React from "react";
import ReactDOM from "react-dom";
import * as serviceWorker from "./serviceWorker";

import Login from "./components/Login";
import { useAuth0 } from "./react-auth0-wrapper";
import Blackout from "./components/Blackout"
import HomePage from "./components/Home"
import Admin from "./containers/Admin"
import Driver from "./containers/Driver"
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import CallbackPage from "./components/Callback";
import Auth from "./components/Auth";

function App() {
  const { loading } = useAuth0();

  if (loading) {
    return (
      <div>Loading...</div>
    );
  }

  let timeoutID;

  function setup() {
    window.addEventListener("mousemove", resetTimer);
    // this.Blackout.addEventListener("mousedown", resetTimer, false);
    // this.Blackout.addEventListener("keypress", resetTimer, false);
    // this.Blackout.addEventListener("DOMMouseScroll", resetTimer, false);
    // this.Blackout.addEventListener("mousewheel", resetTimer, false);
    // this.Blackout.addEventListener("touchmove", resetTimer, false);
    // this.Blackout.addEventListener("MSPointerMove", resetTimer, false);

    startTimer();
}
setup();

function startTimer() {
  // wait 2 seconds before calling goInactive
  timeoutID = window.setTimeout(goInactive, 5000);
}

function resetTimer(e) {
  window.clearTimeout(timeoutID);
  setup();
}

function goInactive() {
  window.addEventListener("mousemove", goActive);
  document.getElementById("blackOutScreen").style.height="100%" 

}

function goActive() {

  document.getElementById("blackOutScreen").style.height="0%" 

}  

return (
    <div className="App">
      <Auth>
        <header>
          <Login />
        </header>
        <Blackout />
        <Router>
              <Switch>
                <Route exact path="/" component={HomePage}/>
                <Route path="/admin" component={Admin}/>
                <Route path="/driver" component={Driver} />
                <Route path="/callback" component={CallbackPage}/>
              </Switch>
            </Router>     
        <Admin />
      </Auth>      
    </div> 
)

}



const rootElement = document.getElementById("root");
ReactDOM.render(<App/>, rootElement);

serviceWorker.unregister();
